__NUXT_JSONP__("/articles/sato_kashiwa", (function(a,b,c,d,e,f,g,h,i,j,k,l,m,n,o,p,q,r){return {data:[{page:{slug:"sato_kashiwa",description:"佐藤可士和展に行って感じた事を雑にまとめました。",title:"佐藤可士和展に行って感じた事",created_at:"2021-02-16T00:00:00.000Z",toc:[{id:m,depth:e,text:m},{id:n,depth:e,text:n},{id:o,depth:e,text:o},{id:p,depth:e,text:p},{id:q,depth:e,text:q}],body:{type:"root",children:[{type:b,tag:d,props:{},children:[{type:a,value:"これは思考の跡なので全然まとまってないです。あしからず。"}]},{type:a,value:c},{type:b,tag:f,props:{id:m},children:[{type:b,tag:g,props:{ariaHidden:h,href:"#%E8%A1%8C%E3%81%8F%E5%89%8D%E3%81%AE%E7%9B%AE%E7%9A%84",tabIndex:i},children:[{type:b,tag:j,props:{className:[k,l]},children:[]}]},{type:a,value:m}]},{type:a,value:c},{type:b,tag:d,props:{},children:[{type:a,value:"巧みに意匠されたロゴデザイン、アイコンはプロダクト、ブランディングにとってどれほどの意味があるのかを知りたい"}]},{type:a,value:c},{type:b,tag:f,props:{id:n},children:[{type:b,tag:g,props:{ariaHidden:h,href:"#%E8%A1%8C%E3%81%A3%E3%81%A6%E6%84%9F%E3%81%98%E3%81%9F%E4%BA%8B",tabIndex:i},children:[{type:b,tag:j,props:{className:[k,l]},children:[]}]},{type:a,value:n}]},{type:a,value:c},{type:b,tag:d,props:{},children:[{type:a,value:"佐藤さんは、ただアイコンや広告を制作する「だけ」の人ではなく、あらゆるものをメディアと捉え、「誰に」「何を」「どのように」届けるのかを徹底的に考え抜いていた人だった。"}]},{type:a,value:c},{type:b,tag:d,props:{},children:[{type:a,value:"巧みに意匠されたロゴやアイコンは、あくまでプロダクトの顔の一部でしかなく、全体のコミュニケーションデザインの一部にしか過ぎない。しかし、そのブランドのいわば「顔」であるロゴやアイコンにブランドの意味を込めて作る事で、ブランドの文化が根付いたり、内部・外部の人間を問わずブランドの特徴を表すようなものになる。"}]},{type:a,value:c},{type:b,tag:f,props:{id:o},children:[{type:b,tag:g,props:{ariaHidden:h,href:"#%E4%BB%8A%E6%B2%BB%E3%82%BF%E3%82%AA%E3%83%AB%E3%81%AE%E8%A9%B1%E3%82%92%E8%81%9E%E3%81%84%E3%81%A6",tabIndex:i},children:[{type:b,tag:j,props:{className:[k,l]},children:[]}]},{type:a,value:o}]},{type:a,value:c},{type:b,tag:d,props:{},children:[{type:a,value:"はじめに今治タオルのブランド戦略を考える仕事を打診された時、佐藤さんは「タオルでどのようにブランド戦略を打ち出していけば良いのか分からない」と思ったそう。しかし、打診と共に送られてきた今治タオルを試しに使ってみたところ、その吸水性の良さに衝撃を受け、「これならいけるかもしれない」と思ったとのこと。"}]},{type:a,value:c},{type:b,tag:d,props:{},children:[{type:a,value:"→ やはり多くの人にプロダクトを届ける為には、プロダクトの価値が高い事が大前提。コミュニケーション・デザインとはつまるところブースターみたいなもので、プロダクトの価値が十分備わっていた時に初めてその効果を発揮するもの。しかし、このブースターがないと多くの人に愛されるプロダクトにはならない。"}]},{type:a,value:c},{type:b,tag:d,props:{},children:[{type:a,value:"・それなしでも成立はするが、あると人々の分かりやすさ・ワクワク感を増大させるものがデザイン"}]},{type:a,value:c},{type:b,tag:f,props:{id:p},children:[{type:b,tag:g,props:{ariaHidden:h,href:"#%E5%AD%A6%E3%82%93%E3%81%A0%E4%BA%8B",tabIndex:i},children:[{type:b,tag:j,props:{className:[k,l]},children:[]}]},{type:a,value:p}]},{type:a,value:c},{type:b,tag:d,props:{},children:[{type:a,value:"デザインとは、プロダクトの価値を直感的に分かりやすく顧客に伝える為の一連のプロセスのこと。"}]},{type:a,value:c},{type:b,tag:d,props:{},children:[{type:a,value:"アイコニックブランディング\n→ 商品、建築、街の風景など、全てがアイコンであると言う考え方"}]},{type:a,value:c},{type:b,tag:d,props:{},children:[{type:a,value:"↑ スマホプロダクトに置き換えると、一つ一つの UI、フォント、アイコン、UX 全てがアイコンであり、プロダクトの価値を伝えるもの。「何を」「誰に」「どのように」伝えたいかを常に意識して、大枠のデザインから細かいデザインまで一貫性を持って作成していく事が必要。"}]},{type:a,value:c},{type:b,tag:d,props:{},children:[{type:a,value:"そして、それぞれのメディアの特性を理解する事が必要。"}]},{type:a,value:c},{type:b,tag:d,props:{},children:[{type:a,value:"ex. 屋外広告はインプが多いかたわら、風景の一部なのでそのまま見過ごされてしまう事も多い"}]},{type:a,value:c},{type:b,tag:d,props:{},children:[{type:a,value:"→ パッと見でわかったら逆効果。一眼見て「良い」違和感を感じさせ、人の目を止める事が出来るのが良い広告(他の広告にも言える事？)"}]},{type:a,value:c},{type:b,tag:d,props:{},children:[{type:a,value:"ex. 逆に商品パッケージなどは一眼で引きつけなければならない。視覚的に「美味しそう」「健康に良さそう」と言ったプロダクト(食品)の価値を一眼で伝えなければならない。"}]},{type:a,value:c},{type:b,tag:d,props:{},children:[{type:a,value:"デザインをする対象の特性や対象人物を考えて、特徴に合ったコミュニケーションを取っていく事が大事"}]},{type:a,value:c},{type:b,tag:f,props:{id:q},children:[{type:b,tag:g,props:{ariaHidden:h,href:"#%E7%94%9F%E3%81%8B%E3%81%97%E3%81%A6%E3%81%84%E3%81%8F%E4%BA%8B",tabIndex:i},children:[{type:b,tag:j,props:{className:[k,l]},children:[]}]},{type:a,value:q}]},{type:a,value:c},{type:b,tag:"ul",props:{},children:[{type:a,value:c},{type:b,tag:r,props:{},children:[{type:a,value:"社会人として生きていく中で、どのようなコミュニケーション戦略をとっていくのか考える"}]},{type:a,value:c},{type:b,tag:r,props:{},children:[{type:a,value:"関わるプロジェクトで、「誰に」「何を」「どのように」届けたいのかを考えて、デザインする"}]},{type:a,value:c}]}]},dir:"\u002Farticles",path:"\u002Farticles\u002Fsato_kashiwa",extension:".md",createdAt:"2021-03-07T15:50:30.608Z",updatedAt:"2021-03-10T02:26:28.141Z"}}],fetch:{},mutations:void 0}}("text","element","\n","p",3,"h3","a","true",-1,"span","icon","icon-link","行く前の目的","行って感じた事","今治タオルの話を聞いて","学んだ事","生かしていく事","li")));